# frozen_string_literal: true

module Jason
  module Math
    module Cryptography
      module KeyAgreement
        # Diffie-Hellman key exchange
        class DiffieHellman
          attr_reader :p, :g

          # rubocop:disable Layout/LineLength
          PARAMETERS = {
            '1536': { # ~88 (?) bits of security
              p: '',
              g: 2,
              prime_length: 192,
              exponent_length: 22 # (?)
            }.freeze,
            '2048': { # 112 bits of security
              p: '',
              g: 2,
              prime_length: 256,
              exponent_length: 28
            }.freeze,
            '3072': { # 128 bits of security
              p: '',
              g: 2,
              prime_length: 384,
              exponent_length: 32
            }.freeze,
            '4096': { # 152 bits of security
              p: '',
              g: 2,
              prime_length: 512,
              exponent_length: 38
            }.freeze,
            '6144': { # 176 bits of security
              p: '',
              g: 2,
              prime_length: 768,
              exponent_length: 44
            }.freeze,
            '8192': { # 200 bits of security
              p: '',
              g: 2,
              prime_length: 1024,
              exponent_length: 50
            }.freeze
          }.freeze
          # rubocop:enable Layout/LineLength

          def initialize(mode, p = nil, g = nil)
            parameters = PARAMETERS[mode]
            @mode = mode

            @p = if p.nil?
                   parameters[:p].nil? ? 
                     NumberTheory.large_random_prime(parameters[:prime_length]) :
                     parameters[:p].to_i(16)
                 else
                   p
                 end

            @g = if g.nil?
                   parameters[:g] || 2
                 else
                   g
                 end

            @exponent_length = parameters[:exponent_length]
          end

          def generate_pair
            key = SecureRandom.random_bytes(@exponent_length).byte_string_to_integer
            shared = NumberTheory.modular_exponentiation(@g, key, @p)

            [key, shared]
          end

          def compute_secret(key, partner_shared)
            NumberTheory.modular_exponentiation(partner_shared, key, @p)
          end
        end
      end
    end
  end
end
